mapper.select:

select t.branchNum, p.purchasefilmNum, p.purchaseDate, p.filmdeadline, f.filmNum, f.filmName, f.filmStory, g.genreName, m.movieimgurl
from theather t, purchasefilm p, film f, movieimg m, genre g
where t.branchNum=1 and p.purchasefilmNum=1 and t.branchNum=p.branchNum and p.filmNum=f.filmNum and f.filmNum=m.movieimgNum and f.genreNum=g.genreNum;
